public MainPage()
  {
      InitializeComponent();
      InitApp();
  }

  private async void InitApp()
  {
      try
      {
          bool inited = await FHClient.Init();
          if(inited)
          {
            //Initialisation is successful
          }
     }
     catch(FHException e)
     {
         //Initialisation failed, handle exception
     }
  }
